import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Updateproduct = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState({
    nameproduct: String,
    descriptionproduct: String,
    quantityproduct: Number,
    image: String, // Ajoutez une clé pour stocker l'image
  });

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/goodiesadmin/${productId}`);
        setProduct(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération du produit:", error);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get(`/api/goodiesadmin/${productId}`);
      const product = response.data;

      // Convertir l'image en base64
      const reader = new FileReader();
      reader.onloadend = async () => {
        const base64Image = reader.result;

        // Envoi des données au serveur
        await axios.put(`/api/goodiesupdate/${productId}`, {
          nameproduct: product.nameproduct,
          descriptionproduct: product.descriptionproduct,
          quantityproduct: product.quantityproduct,
          image: base64Image, // Envoyez l'image en tant que base64
        });
      };
      reader.readAsDataURL(product.image); // Assume que product.image est un objet File
    } catch (error) {
      console.error("Erreur lors de la mise à jour du produit:", error);
    }
  };

  return (
    <div className="container">
      <h1 className="title">Modifier le produit</h1>
      <form
        onSubmit={handleSubmit}
        className="form"
        encType="multipart/form-data"
      >
        {" "}
        {/* Ajoutez l'attribut encType pour gérer les téléchargements de fichiers */}
        <div className="form-group">
          <label className="form-label">Nom du produit:</label>
          <input
            type="text"
            name="nameproduct"
            value={product.nameproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Description:</label>
          <textarea
            name="descriptionproduct"
            value={product.descriptionproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Quantité:</label>
          <input
            type="number"
            name="quantityproduct"
            value={product.quantityproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Image:</label>
          <input
            type="file"
            name="image"
            onChange={(e) =>
              setProduct({ ...product, image: e.target.files[0] })
            } // Mettez à jour le fichier d'image dans l'état lorsqu'il est sélectionné
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Enregistrer
        </button>
      </form>
    </div>
  );
};

export default Updateproduct;
