import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

const GoodiesAdminPage = () => {
  const [products, setProducts] = useState([]);

  /* useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/goodiesadmin");
        setProducts(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des produits:", error);
      }
    };

    fetchProducts();
  }, []);
*/
  return (
    <div>
      <h1>Goodies admin</h1>
      <h2>Liste des produits :</h2>
      <ul>
        {products.map((product) => (
          <li key={product._id}>
            <img src={product.imageproduct} alt={product.nameproduct} />
            <h3>{product.nameproduct}</h3>
            <p>{product.descriptionproduct}</p>
            <p>Quantité : {product.quantityproduct}</p>
            <p>Date : {new Date(product.dateproduct).toLocaleDateString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GoodiesAdminPage;
