// Updateproduct.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const Updatepage = ({ match }) => {
  const [product, setProduct] = useState(null);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(0);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(
          `/api/goodiesadmin/${match.params.id}`
        );
        setProduct(response.data);
        setName(response.data.nameproduct);
        setDescription(response.data.descriptionproduct);
        setQuantity(response.data.quantityproduct);
      } catch (error) {
        console.error("Erreur lors de la récupération du produit:", error);
      }
    };

    fetchProduct();
  }, [match.params.id]);

  const handleUpdate = async () => {
    try {
      await axios.put(`/api/goodiesadmin/${match.params.id}`, {
        name: name,
        description: description,
        quantity: quantity,
      });
      // Rediriger vers une autre page après la mise à jour, si nécessaire
    } catch (error) {
      console.error("Erreur lors de la mise à jour du produit:", error);
    }
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <h1 className="title">Modifier le produit</h1>
      <div className="product-details">
        <label>Nom du produit:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <label>Quantité:</label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <button onClick={handleUpdate}>Mettre à jour</button>
      </div>
    </div>
  );
};

export default Updatepage;
