import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom"; // Import de useHistory pour la navigation

function Loginpage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();

  const changeUsername = (event) => {
    setUsername(event.target.value);
  };

  const changePassword = (event) => {
    setPassword(event.target.value);
  };

  const onSubmit = (event) => {
    event.preventDefault();

    const login = {
      username: username,
      password: password,
    };

    axios
      .post("http://localhost:4000/login", login)
      .then((response) => {
        console.log(response.data);
        if (response.data.success) {
          // Vérifiez si le rôle est "user" avant de rediriger
          if (response.data.role === "user") {
            history.push("/membre");
          }
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });

    setUsername("");
    setPassword("");
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="col">
            <div className="form-div"></div>
            <center>
              <h2 className="bold">Connectez-vous à votre compte</h2>
            </center>
            <form onSubmit={onSubmit}>
              <br />
              <input
                type="text"
                placeholder="Username"
                onChange={changeUsername}
                value={username}
                style={{ height: "80px" }}
                className="form-control form-group"
              />
              <br />
              <input
                type="password"
                placeholder="Password"
                onChange={changePassword}
                value={password}
                style={{ height: "80px" }}
                className="form-control form-group"
              />
              <br />
              <input
                type="submit"
                style={{ height: "50px" }}
                className="btn btn-danger btn-block"
                value="Submit"
              />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Loginpage;
