import React, { useState, useEffect } from "react";
import axios from "axios"; // Assure-toi d'avoir installé axios via npm ou yarn

const Goodiesadminpage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fonction pour récupérer les produits depuis le serveur
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/products"); // Assure-toi d'avoir défini cette route dans ton serveur
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts(); // Appel de la fonction de récupération des produits
  }, []);

  return (
    <div>
      <h1>Goodies admin</h1>
      <ul>
        {products.map((product) => (
          <li key={product._id}>
            <h3>{product.nameproduct}</h3>
            <img src={product.imageproduct} alt={product.nameproduct} />
            <p>{product.descriptionproduct}</p>
            <p>Quantity: {product.quantityproduct}</p>
            <p>Date: {new Date(product.dateproduct).toLocaleDateString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Goodiesadminpage;
