import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

const GoodiesAdminPage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/goodiesadmin");
        setProducts(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des produits:", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="container">
      <h1 className="text-center mt-4 mb-4">Boutique de Goodies Japonais</h1>
      <h2 className="text-center mb-4">Liste des produits :</h2>
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4" key={product._id}>
            <div className="japanese-card">
              <img
                src={product.imageproduct}
                className="japanese-card-img-top"
                alt={product.nameproduct}
              />
              <div className="japanese-card-body">
                <h5 className="japanese-card-title">{product.nameproduct}</h5>
                <hr className="japanese-divider" />
                <p className="japanese-card-text">
                  {product.descriptionproduct}
                </p>
                <hr className="japanese-divider" />
                <p className="japanese-card-text">
                  Quantité disponible : {product.quantityproduct}
                </p>
                <hr className="japanese-divider" />
                <p className="japanese-card-text">
                  Date de disponibilité :{" "}
                  {new Date(product.dateproduct).toLocaleDateString()}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default GoodiesAdminPage;
