import React, { Component } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Importez useNavigate depuis react-router-dom

class Loginpage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
    };

    this.changeUsername = this.changeUsername.bind(this);
    this.changePassword = this.changePassword.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  changeUsername(event) {
    this.setState({
      username: event.target.value,
    });
  }

  changePassword(event) {
    this.setState({
      password: event.target.value,
    });
  }

  onSubmit(event) {
    event.preventDefault();

    const login = {
      username: this.state.username,
      password: this.state.password,
    };
    axios.post("http://127.0.0.1:4000/app/login ", login).then((response) => {
      console.log(response.data);
      // Si la connexion réussit, redirigez vers la page /member
      const navigate = useNavigate(); // Initialisez useNavigate pour effectuer la redirection
      navigate("/member");
    });

    this.setState({
      username: "",
      password: "",
    });
  }

  render() {
    const { username, password } = this.state;
    const isFormValid = username && password;

    return (
      <div>
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="form-div"></div>
              <center>
                <h2 className="bold">Connectez-vous à votre compte</h2>
              </center>
              <form onSubmit={this.onSubmit}>
                <br></br>
                <input
                  type="text"
                  placeholder="Username"
                  onChange={this.changeUsername}
                  value={username}
                  style={{ height: "80px" }}
                  className="form-control form-group"
                />
                <br></br>
                <input
                  type="password"
                  placeholder="Password"
                  onChange={this.changePassword}
                  value={password}
                  style={{ height: "80px" }}
                  className="form-control form-group"
                />
                <br></br>
                <input
                  type="submit"
                  style={{ height: "50px" }}
                  className={`btn btn-danger btn-block ${
                    isFormValid ? "" : "disabled"
                  }`}
                  value="Submit"
                  disabled={!isFormValid}
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Loginpage;
