import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Updateproduct = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState({
    nameproduct: "",
    descriptionproduct: "",
    quantityproduct: 0,
    image: null, // Ajout du champ pour l'image
  });

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/goodiesadmin/${productId}`);
        setProduct(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération du produit:", error);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleChange = (event) => {
    const { name, value, files } = event.target;
    if (name === "image") {
      setProduct({ ...product, [name]: files[0] }); // Récupération du fichier image
    } else {
      setProduct({ ...product, [name]: value });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const formData = new FormData();
      formData.append("nameproduct", product.nameproduct);
      formData.append("descriptionproduct", product.descriptionproduct);
      formData.append("quantityproduct", product.quantityproduct);
      formData.append("image", product.image); // Ajout de l'image à FormData

      await axios.put(`/api/goodiesupdate/${productId}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data", // Spécification du type de contenu pour FormData
        },
      });
    } catch (error) {
      console.error("Erreur lors de la mise à jour du produit:", error);
    }
  };

  return (
    <div className="container">
      <h1 className="title">Modifier le produit</h1>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label className="form-label">Nom du produit:</label>
          <input
            type="text"
            name="nameproduct"
            value={product.nameproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Description:</label>
          <textarea
            name="descriptionproduct"
            value={product.descriptionproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Quantité:</label>
          <input
            type="number"
            name="quantityproduct"
            value={product.quantityproduct}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Image:</label>
          <input
            type="file" // Champ de fichier pour l'image
            name="image"
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Enregistrer
        </button>
      </form>
      <style>
        {`
          .container {
            max-width: 600px;
            margin: auto;
            padding: 20px;
          }
          
          .title {
            font-size: 24px;
            margin-bottom: 20px;
          }
          
          .form {
            width: 100%;
          }
          
          .form-group {
            margin-bottom: 20px;
          }
          
          .form-label {
            display: block;
            font-weight: bold;
          }
          
          .form-control {
            width: 100%;
            padding: 15px;
            font-size: 18px;
            border: 1px solid #ccc;
            border-radius: 4px;
          }
          
          .btn {
            display: inline-block;
            padding: 12px 24px;
            width: 25%;
            font-size: 15px;
            cursor: pointer;
            background-color: #DC143C;
            color: #fff;
            border: none;
            border-radius: 4px;
          }
          
          .btn:hover {
            background-color: rgba(220, 20, 60, 0.8); /* Rouge transparent */
          }
        `}
      </style>
    </div>
  );
};

export default Updateproduct;
