import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

const GoodiesAdminPage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/goodiesadmin");
        setProducts(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des produits:", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Goodies admin</h1>
      <h2>Liste des produits :</h2>
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4" key={product._id}>
            <div className="card mb-3">
              <img
                src={product.imageproduct}
                className="card-img-top"
                alt={product.nameproduct}
              />
              <div className="card-body">
                <h5 className="card-title">{product.nameproduct}</h5>
                <hr />
                <p className="card-text">{product.descriptionproduct}</p>
                <hr />
                <p className="card-text">
                  Quantité : {product.quantityproduct}
                </p>
                <hr />
                <p className="card-text">
                  Date : {new Date(product.dateproduct).toLocaleDateString()}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default GoodiesAdminPage;
